Scripts.OnMouseUp:
if ( this.isMoving ) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end

Scripts.OnMouseDown:
if ( ( ( not this.isLocked ) or ( this.isLocked == 0 ) ) and ( arg1 == "LeftButton" ) ) then
    this:StartMoving();
    this.isMoving = true;
end

Scripts.OnHide:
if ( this.isMoving ) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end

Scripts.OnClick:
						PlaySound("igMainMenuOptionCheckBoxOn");
						if( CP_PlayerListWindow:IsVisible() ) then
    						HideUIPanel( CP_PlayerListWindow );
						else
    						CensusPlus_ShowPlayerList();
						end

Scripts.OnMouseUp:
if ( this.isMoving ) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end

Scripts.OnMouseDown:
if ( ( ( not this.isLocked ) or ( this.isLocked == 0 ) ) and ( arg1 == "LeftButton" ) ) then
    this:StartMoving();
    this.isMoving = true;
end

Scripts.OnHide:
if ( this.isMoving ) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end

Scripts.OnClick:
						if ( this:GetChecked() ) then
							PlaySound("igMainMenuOptionCheckBoxOff");
                            CensusPlus_AutoCloseWho(1);
						else
							PlaySound("igMainMenuOptionCheckBoxOn");
                            CensusPlus_AutoCloseWho(0);
						end

						if ( this:GetChecked() ) then
							PlaySound("igMainMenuOptionCheckBoxOff");
                            CensusPlus_AutoStart(1);
						else
							PlaySound("igMainMenuOptionCheckBoxOn");
                            CensusPlus_AutoStart(0);
						end

if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
	CensusPlus_Verbose();
else
	PlaySound("igMainMenuOptionCheckBoxOn");
	CensusPlus_Verbose();
end

if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
	CensusPlus_SetAutoCensus(1);
else
	PlaySound("igMainMenuOptionCheckBoxOn");
	CensusPlus_SetAutoCensus(0);
end

if ( this:GetChecked() ) then
	PlaySound("igMainMenuOptionCheckBoxOff");
	CensusPlus_DoThisCharacter = 1;
else
	PlaySound("igMainMenuOptionCheckBoxOn");
	CensusPlus_DoThisCharacter = 0;
end

						if ( this:GetChecked() ) then
							PlaySound("igMainMenuOptionCheckBoxOff");
                            CensusPlus_Database["Info"]["CensusButtonShown"] = 1;
                            CensusButtonFrame:Show();
						else
							PlaySound("igMainMenuOptionCheckBoxOn");
                            CensusPlus_Database["Info"]["CensusButtonShown"] = 0;
                            CensusButtonFrame:Hide();
						end

						if ( this:GetChecked() ) then
							PlaySound("igMainMenuOptionCheckBoxOff");
                            CensusPlus_PerCharInfo["PlayFinishSound"] = true;
						else
							PlaySound("igMainMenuOptionCheckBoxOn");
                            CensusPlus_PerCharInfo["PlayFinishSound"] = false;
						end

Scripts.OnMouseUp:
if ( this.isMoving ) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end

Scripts.OnMouseDown:
if ( ( ( not this.isLocked ) or ( this.isLocked == 0 ) ) and ( arg1 == "LeftButton" ) ) then
    this:StartMoving();
    this.isMoving = true;
end

Scripts.OnHide:
if ( this.isMoving ) then
    this:StopMovingOrSizing();
    this.isMoving = false;
end
