Scripts.OnMouseUp:
if LVBMOptionsFrame:IsShown() then
	HideUIPanel(LVBMOptionsFrame);
	this:SetText(LVBMGUI_SHOW_OPTIONS);
else
	ShowUIPanel(LVBMOptionsFrame);
	this:SetText(LVBMGUI_HIDE_OPTIONS);
end

Scripts.OnLoad:
this:RegisterEvent("PLAYER_LOGIN");
this:RegisterEvent("RAID_ROSTER_UPDATE");
this:RegisterEvent("PLAYER_LOGOUT");

LVBMBossModFrame.lastSelectedBossMod = {};
PanelTemplates_SetNumTabs(this, LVBMGUI_MAINFRAME_TABCOUNT);	-- Anzahl Tabs // Naxx, AQ, etc.
LVBMBossModFrame.selectedTab = 1;
LVBMBossModFrame.showPage = 1;
PanelTemplates_UpdateTabs(this);

LVBMBossModFrame.selectedBossMod = 0;

for i, value in pairs(LVBMGUI_FRAME_LOADADDON_DESCRIPTION) do
	LVBMBossModListFrameLoadAddOnInfo:SetText( 
		LVBMBossModListFrameLoadAddOnInfo:GetText().."\n"..value
	);
end

Scripts.OnShow:
LVBMBossModFrame_OnShow();

PanelTemplates_SetNumTabs(this, LVBMGUI_MAINFRAME_TABCOUNT);
for i=1, LVBMGUI_MAINFRAME_TABCOUNT, 1 do
	getglobal(this:GetName().."Tab"..i):Show();
	getglobal(this:GetName().."Tab"..i):SetText(LVGUI.MainFrameTabs[i].Title);
	PanelTemplates_TabResize(0, getglobal(this:GetName().."Tab"..i), nil, nil);
end
PanelTemplates_UpdateTabs(this);

Scripts.OnMouseUp:
local syncedBars = false;
for index, value in pairs(LVBM.StatusBarData) do
	if value.syncedBy then
		LVBM.AddMsg(index..": "..value.syncedBy);
		syncedBars = true;
	end
end
if( syncedBars == false ) then
	LVBM.AddMsg(LVBMGUI_BUTTON_STATUSBAR_SYNCINFO_FAILD);
end

Scripts.OnValueChanged:
