Scripts.OnLoad:
UIDropDownMenu_Initialize(this, function()
	for index,value in pairs(LVBMStatusBars_Designs) do
		local info = {};
		info.func = function()
			LVBM_Gui_ChangeExampleBarDesign(this:GetID(), true);
			HideUIPanel(LVBMOptionsFrame);
			ShowUIPanel(LVBMOptionsFrame);
		end;
		info.text = value.name;
		UIDropDownMenu_AddButton(info);
	end
end);
UIDropDownMenu_SetWidth(100);
UIDropDownMenu_SetSelectedID(this, LVBM.Options.StatusBarDesign);

Scripts.OnMouseUp:
if( this:GetChecked() ) then
	LVBM.Options.EnableStatusBars = false;
	for index, value in pairs(LVBM.StatusBarData) do	-- hide current Bars
		LVBM.EndStatusBarTimer(index, true, nil, true);
	end
else 
	LVBM.Options.EnableStatusBars = true;
end

if( this:GetChecked() ) then
	LVBM.Options.FillUpStatusBars = false;
else 
	LVBM.Options.FillUpStatusBars = true;
end

if( this:GetChecked() ) then
	LVBM.Options.StatusBarsFlippedOver = false;
else 
	LVBM.Options.StatusBarsFlippedOver = true;
end
LVBMStatusBars_FlipOver();

if( this:GetChecked() ) then
	LVBM.Options.FlashBars = false;
else 
	LVBM.Options.FlashBars = true;
end

if( this:GetChecked() ) then
	LVBM.Options.AutoColorBars = false;
	for index, value in pairs(LVBM.StatusBarData) do
		if value.frame.specialColor then
			getglobal(value.frame:GetName().."Bar"):SetStatusBarColor(value.color.R, value.color.G, value.color.B, value.color.A);
		else
			getglobal(value.frame:GetName().."Bar"):SetStatusBarColor(LVBM.Options.StatusBarColor.r, LVBM.Options.StatusBarColor.g, LVBM.Options.StatusBarColor.b, LVBM.Options.StatusBarColor.a);
		end
	end
else 
	LVBM.Options.AutoColorBars = true;
end

Scripts.OnColorSelect:
local r, g, b = this:GetColorRGB();
LVBM.Options.StatusBarColor.r = r;
LVBM.Options.StatusBarColor.g = g;
LVBM.Options.StatusBarColor.b = b;
getglobal(this:GetParent():GetName().."StatusBarTimerExampleBar"):SetStatusBarColor(r,g,b);

LVBMGuiUpdateStatusbars();

Scripts.OnValueChanged:
local r, g, b, a = getglobal(this:GetParent():GetName().."StatusBarTimerExampleBar"):GetStatusBarColor();
LVBM.Options.StatusBarColor.a = this:GetValue();
getglobal(this:GetParent():GetName().."StatusBarTimerExampleBar"):SetStatusBarColor(r,g,b, this:GetValue());

LVBMGuiUpdateStatusbars();

getglobal(this:GetName().."Value"):SetText(string.format("%.3f", this:GetValue()));
LVBM.Options.StatusBarSize.Scale = LVBM.math.round(this:GetValue(), 3);
LVBMStatusBars_Resize();

if this.ShowTestMsg then
	LVBM.StartStatusBarTimer(10, "test", true);
end
this.ShowTestMsg = true;

getglobal(this:GetName().."Value"):SetText(string.format("%.0f", this:GetValue()));
LVBM.Options.StatusBarSize.Width = LVBM.math.round(this:GetValue());
LVBMStatusBars_Resize();

if this.ShowTestMsg then
	LVBM.StartStatusBarTimer(10, "test", true);
end
this.ShowTestMsg = true;

getglobal(this:GetName().."Value"):SetText(string.format("%d", this:GetValue()));
LVBM.Options.MaxStatusBars = LVBM.math.round(this:GetValue());

--[[
if this.ShowTestMsg then
	for i=1, 30, 1 do
		LVBM.EndStatusBarTimer(i..". test", true);
	end
	for i=1, LVBM.Options.MaxStatusBars, 1 do
		LVBM.StartStatusBarTimer(3, i..". test", true);
	end
end  --]]

this.ShowTestMsg = true;

Scripts.OnMouseUp:
LVBM.Options.Gui.PizzaTimerText = getglobal(this:GetParent():GetName().."PizzaBoxName"):GetText(); 
LVBM.Options.Gui.PizzaTimerMin = getglobal(this:GetParent():GetName().."PizzaBoxMin"):GetNumber();
LVBM.Options.Gui.PizzaTimerSec = getglobal(this:GetParent():GetName().."PizzaBoxSec"):GetNumber();	
if( LVBM.Rank > 0 ) then
else
	LVBM.Options.Gui.PizzaTimerBroadcast = false;
end

LVBM.StartStatusBarTimer(
	((LVBM.Options.Gui.PizzaTimerMin * 60 ) +
	  LVBM.Options.Gui.PizzaTimerSec),
	LVBM.Options.Gui.PizzaTimerText, 
	not LVBM.Options.Gui.PizzaTimerBroadcast
)
if ((LVBM.Options.Gui.PizzaTimerMin * 60 ) + LVBM.Options.Gui.PizzaTimerSec) > 10 then 
	if LVBM.Options.Gui.PizzaTimerBroadcast then
		LVBM.Schedule(((LVBM.Options.Gui.PizzaTimerMin * 60 ) + LVBM.Options.Gui.PizzaTimerSec) - 10, "LVBM.Announce", string.format(LVBM_TIMER_IS_ABOUT_TO_EXPIRE, LVBM.Options.Gui.PizzaTimerText));
	else
		LVBM.Schedule(((LVBM.Options.Gui.PizzaTimerMin * 60 ) + LVBM.Options.Gui.PizzaTimerSec) - 10, "LVBM.AddSpecialWarning", string.format(LVBM_TIMER_IS_ABOUT_TO_EXPIRE, LVBM.Options.Gui.PizzaTimerText));
	end
end

LVBMStatusBars_SetDefaultValues();

HideUIPanel(LVBMOptionsFrame);
ShowUIPanel(LVBMOptionsFrame);

