Scripts.OnEnter:
GameTooltip:Hide();
GameTooltip:ClearLines();
GameTooltip:SetOwner(this, "ANCHOR_CURSOR");
GameTooltip:SetText(LVBMGUI_EDITBOX_AUTORESPOND_TOOLTIP_HEADER);
local i = 1;
while( getglobal("LVBMGUI_EDITBOX_AUTORESPOND_TOOLTIP_TEXT"..i) ) do
	GameTooltip:AddDoubleLine(getglobal("LVBMGUI_EDITBOX_AUTORESPOND_TOOLTIP_TEXT"..i)[1], 
				  getglobal("LVBMGUI_EDITBOX_AUTORESPOND_TOOLTIP_TEXT"..i)[2], 
				  NORMAL_FONT_COLOR.r,
				  NORMAL_FONT_COLOR.g, 
				  NORMAL_FONT_COLOR.b, 
				  HIGHLIGHT_FONT_COLOR.r, 
				  HIGHLIGHT_FONT_COLOR.g,
				  HIGHLIGHT_FONT_COLOR.b);
	i = i + 1;
end
GameTooltip:Show()

