end

Scripts.OnMouseUp:
LVBM.Options.Gui.HidePlayerNamesInRaid = not this:GetChecked();

if (LVBM.Options.Gui.HidePlayerNamesInRaid 
   and tonumber(GetCVar("UnitNamePlayer")) == 1 
   and GetNumRaidMembers() > 0) then

	LVBM.Options.Gui["HidePlayerNames"] = true;
	SetCVar("UnitNamePlayer", 0);

elseif (not LVBM.Options.Gui.HidePlayerNamesInRaid
   and LVBM.Options.Gui["HidePlayerNames"]) then

	LVBM.Options.Gui["HidePlayerNames"] = false;
	SetCVar("UnitNamePlayer", 1);
end

