	LVBM.SetCombatLogDistance( this:GetValue() );
	LVBM.Options.Gui.CombatLogValue = LVBM.math.round(this:GetValue());
	getglobal(this:GetName().."Value"):SetText(LVBM.math.round(this:GetValue()));
end

this.ShowTestMsg = true

