Scripts.OnClick:
if this:GetChecked() then
	SW_Settings["SY_AutoVote"] = 1;
	if SW_AutoVoteNo:GetChecked() then
		SW_AutoVoteNo:SetChecked(false);
	end
else
	if SW_AutoVoteNo:GetChecked() then
		SW_Settings["SY_AutoVote"] = 0;
	else
		SW_Settings["SY_AutoVote"] = nil;
	end
end

